import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters?: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);

  // User name stored locally
  const [name, setName] = useState(
    localStorage.getItem("poll_user_name") || ""
  );

  const fetchPoll = useCallback(async () => {
    // load question
    const { data: qData } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    // load options
    const { data: oData } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    // load votes
    const { data: votesData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    // merge names into options
    const merged =
      (oData || []).map((opt: any) => ({
        ...opt,
        voters:
          votesData?.filter((v) => v.option_id === opt.id).map((v) => v.name) ??
          [],
      })) || [];

    setOptions(merged);

    // find user's vote
    const { data: vData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (vData) setUserVote(vData.option_id);
  }, [questionId, userId]);

  // realtime subscription
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-${questionId}`)
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "poll_options",
        },
        () => {
          fetchPoll();
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [questionId, fetchPoll]);

  const handleVote = async (option: PollOption) => {
    if (!name.trim()) {
      setFeedback("Please enter your name before voting.");
      return;
    }

    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    const previousVote = userVote;

    // If clicking same option, ignore
    if (previousVote === option.id) return;

    // STEP 1 — remove old vote row COMPLETELY (removes name from old option)
    if (previousVote) {
      await supabase
        .from("poll_votes")
        .delete()
        .eq("user_id", userId)
        .eq("question_id", questionId);

      await supabase.rpc("decrement_vote", { option_id: previousVote });
    }

    // STEP 2 — add new vote & name
    await supabase.rpc("increment_vote", { option_id: option.id });

    await supabase.from("poll_votes").insert({
      question_id: questionId,
      user_id: userId,
      option_id: option.id,
      name: name,
    });

    setUserVote(option.id);
    fetchPoll();
  };

  const totalVotes = options.reduce((s, o) => s + o.votes, 0);

  return (
    <div className="poll-container">
      <h3 className="poll-question">{question}</h3>

      {/* name entry box */}
      {!localStorage.getItem("poll_user_name") && (
        <div className="name-entry-box">
          <input
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button
            onClick={() => {
              if (name.trim()) {
                localStorage.setItem("poll_user_name", name);
              }
            }}
          >
            Save
          </button>
        </div>
      )}

      <div className="poll-options-grid">
        {options.map((option) => {
          const percent =
            totalVotes === 0
              ? 0
              : Math.round((option.votes / totalVotes) * 100);

          const isChosen = userVote === option.id;

          return (
            <div
              key={option.id}
              className={`poll-option-card ${isChosen ? "selected" : ""}`}
              onClick={() => handleVote(option)}
            >
              <p className="option-text">{option.option_text}</p>
              <p className="option-percent">{percent}%</p>

              {option.voters && option.voters.length > 0 && (
                <p className="voters-list">
                  <strong>Chosen by:</strong> {option.voters.join(", ")}
                </p>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
