import { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";
import "./CommentSection.css";

export default function CommentSection({ page }) {
  const [comments, setComments] = useState([]);
  const [username, setUsername] = useState("");
  const [newComment, setNewComment] = useState("");

  // Fetch comments whenever page changes
  useEffect(() => {
    const fetchComments = async () => {
      const { data, error } = await supabase
        .from("comments")
        .select("*")
        .eq("page", page)
        .order("created_at", { ascending: false });

      if (error) console.error("Error fetching comments:", error);
      else setComments(data);
    };

    fetchComments();
  }, [page]);

  // Handle posting a new comment
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username.trim() || !newComment.trim()) return;

    const { data, error } = await supabase
      .from("comments")
      .insert([{ page, username, content: newComment }])
      .select(); // return inserted row

    if (error) console.error("Error posting comment:", error);
    else {
      setComments([data[0], ...comments]);
      setNewComment("");
      setUsername("");
    }
  };

  return (
    <div className="comment-section">
      <h2>Comments</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Your name"
        />
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Write a comment..."
        />
        <button type="submit">Post</button>
      </form>

      <div className="comments-list">
        {comments.length === 0 ? (
          <p>No comments yet. Be the first to comment!</p>
        ) : (
          comments.map((comment) => (
            <div key={comment.id} className="comment">
              <strong>{comment.username}:</strong> <span>{comment.content}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
