import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [userName, setUserName] = useState<string>("");

  // Prompt name ONCE per page
  useEffect(() => {
    const key = `poll_username_q${questionId}`;
    let stored = localStorage.getItem(key);

    if (!stored) {
      stored = prompt("Enter your name:") || "Anonymous";
      localStorage.setItem(key, stored);
    }

    setUserName(stored);
  }, [questionId]);

  // Fetch poll state
  const fetchPoll = useCallback(async () => {
    const { data: qData } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    const { data: optionsData } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    const { data: votesData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    const mergedOptions: PollOption[] = (optionsData || []).map((opt) => ({
      ...opt,
      voters:
        votesData
          ?.filter((v) => v.option_id === opt.id)
          .map((v) => v.user_name) || [],
    }));

    setOptions(mergedOptions);

    const { data: myVote } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (myVote) setUserVote(myVote.option_id);
  }, [questionId, userId]);

  // Realtime listener
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-updates-${questionId}`)
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "poll_votes",
        },
        () => fetchPoll()
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [fetchPoll, questionId]);

  // Handle votem
  const handleVote = async (option: PollOption) => {
  setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

  // if clicking the same option again, treat it as "remove vote"
  if (userVote === option.id) {
    // remove from poll_votes
    await supabase
      .from("poll_votes")
      .delete()
      .eq("question_id", questionId)
      .eq("user_id", userId);

    // decrement vote
    await supabase.rpc("decrement_vote", { option_id: option.id });

    setUserVote(null);
    await fetchPoll(); // refresh UI
    return;
  }

  // switching vote: remove old vote
  if (userVote !== null) {
    await supabase.rpc("remove_previous_vote_for_user", {
      in_user_id: userId,
  in_question_id: questionId
});


    await supabase.rpc("decrement_vote", { option_id: userVote });
  }

  // insert new vote
  await supabase.from("poll_votes").insert({
    question_id: questionId,
    option_id: option.id,
    user_id: userId,
    user_name: userName,
  });

  // increment new vote
  await supabase.rpc("increment_vote", { option_id: option.id });

  setUserVote(option.id);
  await fetchPoll();
};

  const totalVotes = options.reduce((s, o) => s + o.votes, 0);

  return (
    <div className="poll-wrapper">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options">
        {options.map((opt) => {
          const percent = totalVotes ? Math.round((opt.votes / totalVotes) * 100) : 0;
          const isChosen = opt.id === userVote;

          return (
            <div
              key={opt.id}
              className={`poll-card ${isChosen ? "selected" : ""}`}
              onClick={() => handleVote(opt)}
            >
              <div className="poll-top">
                <span className="poll-text">{opt.option_text}</span>
                <span className="poll-percent">{percent}%</span>
              </div>

              <div className="poll-bar">
                <div className="poll-bar-fill" style={{ width: `${percent}%` }} />
              </div>

              {opt.voters.length > 0 && (
                <div className="poll-voters">
                  <strong>Chosen by:</strong> {opt.voters.join(", ")}
                </div>
              )}

              {isChosen && (
                <div className="your-vote">✓ You ({userName}) voted here</div>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
