import React, { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";

interface PollOption {
  id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState<string>("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);

  const fetchPoll = async () => {
    // question
    const { data: qData } = await supabase
      .from("poll_questions")
      .select()
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    // options
    const { data: oData } = await supabase
      .from("poll_options")
      .select()
      .eq("question_id", questionId)
      .order("id");

    if (oData) setOptions(oData as PollOption[]);

    // user vote
    const { data: vData } = await supabase
      .from("poll_votes")
      .select()
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (vData) setUserVote(vData.option_id);
  };

  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-${questionId}`)
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "poll_options",
          filter: `question_id=eq.${questionId}`,
        },
        (payload) => {
          if (payload.eventType === "UPDATE") {
            setOptions((prev) =>
              prev.map((opt) =>
                opt.id === payload.new.id ? payload.new : opt
              )
            );
          }
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [questionId]);

  const handleVote = async (option: PollOption) => {
    // show correct/incorrect
    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    if (userVote === option.id) return;

    if (userVote)
      await supabase.rpc("decrement_vote", { option_id: userVote });

    await supabase.rpc("increment_vote", { option_id: option.id });

    await supabase.from("poll_votes").upsert({
      question_id: questionId,
      user_id: userId,
      option_id: option.id,
    });

    setUserVote(option.id);
  };

  return (
    <div className="poll-container">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options">
        {options.map((option) => {
          const selected = userVote === option.id;

          return (
            <button
              key={option.id}
              onClick={() => handleVote(option)}
              className={`poll-option ${selected ? "selected" : ""}`}
            >
              <span>{option.option_text}</span>
              <span className="votes">{option.votes} votes</span>
            </button>
          );
        })}
      </div>

      {feedback && <p className="feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
