import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters?: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [userName, setUserName] = useState("");

  // -------------------------------
  // PROMPT USER FOR NAME
  // -------------------------------
  const promptForName = () => {
    let name = "";
    while (!name) {
      name = window.prompt("Please enter your name to vote:")?.trim() || "";
    }
    localStorage.setItem("voter_name", name);
    setUserName(name);
  };

  // Load name or ask for it
  useEffect(() => {
    const stored = localStorage.getItem("voter_name");
    if (!stored) {
      promptForName();
    } else {
      setUserName(stored);
    }
  }, []);

  // -------------------------------
  // FETCH POLL DATA
  // -------------------------------
  const fetchPoll = useCallback(async () => {
    // fetch question
    const { data: qData } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    // fetch options
    const { data: optionsData } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    // fetch all votes
    const { data: votesData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    // attach voters
    const merged =
      optionsData?.map((opt: any) => ({
        ...opt,
        voters:
          votesData
            ?.filter((v) => v.option_id === opt.id)
            .map((v) => v.user_name) || [],
      })) || [];

    setOptions(merged);

    // fetch THIS user's vote
    const { data: vData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (vData) setUserVote(vData.option_id);
    else setUserVote(null);
  }, [questionId, userId]);

  // -------------------------------
  // INITIAL + REALTIME
  // -------------------------------
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-${questionId}`)
      .on(
        "postgres_changes",
        { event: "UPDATE", schema: "public", table: "poll_options" },
        (payload) => {
          const updated = payload.new as PollOption;
          if (updated.question_id !== questionId) return;

          setOptions((prev) =>
            prev.map((o) => (o.id === updated.id ? { ...o, ...updated } : o))
          );
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [questionId, fetchPoll]);

  // -------------------------------
  // HANDLE VOTE
  // -------------------------------
  const handleVote = async (option: PollOption) => {
    // re-ask name if missing
    if (!userName) promptForName();

    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    // -----------------------
    // If clicking same option → remove vote
    // -----------------------
    if (userVote === option.id) {
      await supabase
        .from("poll_votes")
        .delete()
        .eq("question_id", questionId)
        .eq("user_id", userId);

      await supabase.rpc("decrement_vote", { option_id: option.id });

      // update client UI
      setUserVote(null);

      await fetchPoll();
      return;
    }

    // -----------------------
    // If switching votes
    // -----------------------
    if (userVote !== null) {
      // remove old vote
      await supabase
        .from("poll_votes")
        .delete()
        .eq("question_id", questionId)
        .eq("user_id", userId);

      await supabase.rpc("decrement_vote", { option_id: userVote });
    }

    // insert new vote with name
    await supabase.from("poll_votes").insert({
      question_id: questionId,
      option_id: option.id,
      user_id: userId,
      user_name: userName,
    });

    await supabase.rpc("increment_vote", { option_id: option.id });

    setUserVote(option.id);
    await fetchPoll();
  };

  // total votes for percentages
  const totalVotes = options.reduce((sum, o) => sum + o.votes, 0);

  return (
    <div className="poll-container">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options-grid">
        {options.map((option) => {
          const percent =
            totalVotes === 0 ? 0 : Math.round((option.votes / totalVotes) * 100);
          const selected = userVote === option.id;

          return (
            <div
              key={option.id}
              className={`poll-option-card ${selected ? "selected" : ""}`}
              onClick={() => handleVote(option)}
            >
              <p className="option-text">{option.option_text}</p>
              <p className="option-votes">{percent}%</p>

              {option.voters && option.voters.length > 0 && (
                <p className="voters-list">
                  <strong>Chosen by:</strong> {option.voters.join(", ")}
                </p>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
