import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters?: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [userName, setUserName] = useState<string>("");

  // Ask for name ONCE per question page
  useEffect(() => {
    const key = `poll_name_page_${questionId}`;
    let name = localStorage.getItem(key);

    if (!name) {
      name = prompt("Enter your name:") || "Anonymous";
      localStorage.setItem(key, name);
    }

    setUserName(name);
  }, [questionId]);

  // Fetch poll data
  const fetchPoll = useCallback(async () => {
    const { data: qData } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    const { data: oData } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    const { data: vData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    const merged = (oData || []).map((opt) => ({
      ...opt,
      voters:
        vData
          ?.filter((v) => v.option_id === opt.id)
          .map((v) => v.user_name) ?? [],
    }));

    setOptions(merged);

    const { data: myVote } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (myVote) setUserVote(myVote.option_id);
  }, [questionId, userId]);

  // Realtime listener
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-realtime-${questionId}`)
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "poll_options",
        },
        () => fetchPoll()
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [fetchPoll, questionId]);

  const handleVote = async (option: PollOption) => {
    if (!userName) return;

    // highlight correct / incorrect
    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    if (userVote === option.id) return;

    // remove old vote
    if (userVote) {
      await supabase.rpc("decrement_vote", { option_id: userVote });
      await supabase
        .from("poll_votes")
        .delete()
        .eq("user_id", userId)
        .eq("question_id", questionId);
    }

    // add new vote
    await supabase.rpc("increment_vote", { option_id: option.id });
    await supabase.from("poll_votes").insert({
      question_id: questionId,
      option_id: option.id,
      user_id: userId,
      user_name: userName,
    });

    setUserVote(option.id);
    fetchPoll();
  };

  const totalVotes = options.reduce((s, o) => s + o.votes, 0);

  return (
    <div className="poll-wrapper">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options">
        {options.map((opt) => {
          const isChosen = opt.id === userVote;
          const percent =
            totalVotes === 0 ? 0 : Math.round((opt.votes / totalVotes) * 100);

          return (
            <div
              key={opt.id}
              className={`poll-card ${isChosen ? "selected" : ""}`}
              onClick={() => handleVote(opt)}
            >
              <div className="poll-top">
                <span className="poll-text">{opt.option_text}</span>
                <span className="poll-percent">{percent}%</span>
              </div>

              {/* Pretty percentage bar */}
              <div className="poll-bar">
                <div
                  className="poll-bar-fill"
                  style={{ width: `${percent}%` }}
                ></div>
              </div>

              {/* Show USERS' NAMES */}
              {opt.voters && opt.voters.length > 0 && (
                <div className="poll-voters">
                  <strong>Chosen by:</strong> {opt.voters.join(", ")}
                </div>
              )}

              {/* Highlight user name inside the card */}
              {isChosen && (
                <div className="your-vote">
                  ✓ You ({userName}) chose this
                </div>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
