import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters?: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);

  // fetch everything
  const fetchPoll = useCallback(async () => {
    const { data: q } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();
    if (q) setQuestion(q.question_text);

    const { data: opts } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    const { data: votes } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    const merged =
      (opts || []).map((opt: any) => ({
        ...opt,
        voters: votes
          ?.filter((v) => v.option_id === opt.id)
          .map((v) => v.name) ?? [],
      })) || [];

    setOptions(merged);

    const { data: myVote } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (myVote) setUserVote(myVote.option_id);
  }, [questionId, userId]);

  // realtime listener (poll_options + poll_votes)
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-${questionId}`)
      .on(
        "postgres_changes",
        { event: "*", schema: "public", table: "poll_options" },
        () => fetchPoll()
      )
      .on(
        "postgres_changes",
        { event: "*", schema: "public", table: "poll_votes" },
        () => fetchPoll()
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [fetchPoll, questionId]);

  const handleVote = async (option: PollOption) => {
    const name = prompt("Enter your name to submit your vote:");

    if (!name || !name.trim()) {
      setFeedback("Name required to vote.");
      return;
    }

    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    // remove old vote
    if (userVote) {
      await supabase.from("poll_votes")
        .delete()
        .eq("question_id", questionId)
        .eq("user_id", userId);

      await supabase.rpc("decrement_vote", { option_id: userVote });
    }

    // add new vote
    await supabase.rpc("increment_vote", { option_id: option.id });

    await supabase.from("poll_votes").insert({
      question_id: questionId,
      user_id: userId,
      option_id: option.id,
      name,
    });

    setUserVote(option.id);
    fetchPoll();
  };

  const totalVotes = options.reduce((s, o) => s + o.votes, 0);

  return (
    <div className="poll-container">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options-grid">
        {options.map((option) => {
          const percent =
            totalVotes === 0 ? 0 : Math.round((option.votes / totalVotes) * 100);
          const isChosen = userVote === option.id;

          return (
            <div
              key={option.id}
              className={`poll-option-card ${isChosen ? "selected" : ""}`}
              onClick={() => handleVote(option)}
            >
              <p className="option-text">{option.option_text}</p>

              <p className="option-percent">{percent}%</p>

              {option.voters && option.voters.length > 0 && (
                <p className="voters-list">
                  <strong>Chosen by:</strong> {option.voters.join(", ")}
                </p>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
