import React, { useEffect, useState, useCallback } from "react";
import { supabase } from "../supabaseClient";
import "./PollQuestion.css";

interface PollOption {
  id: number;
  question_id: number;
  option_text: string;
  votes: number;
  is_correct: boolean;
  voters?: string[];
}

interface PollQuestionProps {
  questionId: number;
  userId: string;
}

const PollQuestion: React.FC<PollQuestionProps> = ({ questionId, userId }) => {
  const [question, setQuestion] = useState("");
  const [options, setOptions] = useState<PollOption[]>([]);
  const [userVote, setUserVote] = useState<number | null>(null);
  const [feedback, setFeedback] = useState<string | null>(null);
  const [userName, setUserName] = useState<string | null>(null);

  // Ask for name once per page
  useEffect(() => {
    const key = `poll_name_page_${questionId}`;
    let name = localStorage.getItem(key);

    if (!name) {
      name = prompt("Enter your name to vote:") || "Anonymous";
      localStorage.setItem(key, name);
    }

    setUserName(name);
  }, [questionId]);

  // Fetch question + options + votes
  const fetchPoll = useCallback(async () => {
    const { data: qData } = await supabase
      .from("poll_questions")
      .select("*")
      .eq("id", questionId)
      .single();

    if (qData) setQuestion(qData.question_text);

    const { data: optionData } = await supabase
      .from("poll_options")
      .select("*")
      .eq("question_id", questionId)
      .order("id");

    const { data: voteData } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId);

    const mergedOptions =
      (optionData || []).map((opt: any) => ({
        ...opt,
        voters:
          voteData
            ?.filter((v) => v.option_id === opt.id)
            .map((v) => v.user_name) ?? [],
      })) || [];

    setOptions(mergedOptions);

    const { data: myVote } = await supabase
      .from("poll_votes")
      .select("*")
      .eq("question_id", questionId)
      .eq("user_id", userId)
      .maybeSingle();

    if (myVote) setUserVote(myVote.option_id);
  }, [questionId, userId]);

  // Realtime listener
  useEffect(() => {
    fetchPoll();

    const channel = supabase
      .channel(`poll-${questionId}`)
      .on(
        "postgres_changes",
        {
          event: "*",
          schema: "public",
          table: "poll_options",
        },
        (payload) => {
          const updated = payload.new as PollOption;

          if (updated.question_id !== questionId) return;

          setOptions((prev) =>
            prev.map((o) =>
              o.id === updated.id ? { ...o, ...updated } : o
            )
          );
        }
      )
      .subscribe();

    return () => {
      supabase.removeChannel(channel);
    };
  }, [questionId, fetchPoll]);

  // Handle vote
  const handleVote = async (option: PollOption) => {
    if (!userName) return;

    setFeedback(option.is_correct ? "Correct!" : "Incorrect — try again!");

    // If the vote is the same, do nothing
    if (userVote === option.id) return;

    // 1. Remove old vote (decrement old option and remove from poll_votes)
    if (userVote) {
      await supabase.rpc("decrement_vote", { option_id: userVote });
      await supabase
        .from("poll_votes")
        .delete()
        .eq("question_id", questionId)
        .eq("user_id", userId);
    }

    // 2. Add new vote (increment and insert)
    await supabase.rpc("increment_vote", { option_id: option.id });

    await supabase.from("poll_votes").insert({
      question_id: questionId,
      option_id: option.id,
      user_id: userId,
      user_name: userName,
    });

    setUserVote(option.id);

    // Refresh voter list immediately
    await fetchPoll();
  };

  const totalVotes = options.reduce((sum, o) => sum + o.votes, 0);

  return (
    <div className="poll-container">
      <h3 className="poll-question">{question}</h3>

      <div className="poll-options-grid">
        {options.map((option) => {
          const percent =
            totalVotes === 0 ? 0 : Math.round((option.votes / totalVotes) * 100);

          const isChosen = userVote === option.id;

          return (
            <div
              key={option.id}
              className={`poll-option-card ${isChosen ? "selected" : ""}`}
              onClick={() => handleVote(option)}
            >
              <div className="option-header">
                <span>{option.option_text}</span>
                <span className="poll-percent">{percent}%</span>
              </div>

              {option.voters && option.voters.length > 0 && (
                <p className="voters">
                  <strong>Chosen by:</strong> {option.voters.join(", ")}
                </p>
              )}
            </div>
          );
        })}
      </div>

      {feedback && <p className="poll-feedback">{feedback}</p>}
    </div>
  );
};

export default PollQuestion;
